/* MODULE Acelerometro. */

#include "Acelerometro.h"

#define Acelerometro_CPU_IS_LITTLE_ENDIAN 1 /* Cpu is little endian */

/* External 3-axis accelerometer control register addresses */
#define Acelerometro_CTRL_REG_1 0x2A
/* MMA8451 3-axis accelerometer control register bit masks */
#define Acelerometro_ACTIVE_BIT_MASK 0x01
#define Acelerometro_F_READ_BIT_MASK 0x02

/* External 3-axis accelerometer data register addresses */
#define Acelerometro_OUT_X_MSB 0x01
#define Acelerometro_OUT_X_LSB 0x02
#define Acelerometro_OUT_Y_MSB 0x03
#define Acelerometro_OUT_Y_LSB 0x04
#define Acelerometro_OUT_Z_MSB 0x05
#define Acelerometro_OUT_Z_LSB 0x06

#define Acelerometro_I2C_ADDR   (0x1D) /* I2C slave device address as set in the properties */

typedef struct {
  int16_t NxOff; /* offset for X axis */
  int16_t NyOff; /* offset for Y axis */
  int16_t NzOff; /* offset for Z axis */
} tAccelCal;

/* default calibration values from component properties */
static const tAccelCal InitialCalibration = { /* Initial default calibration values */
  0, /* X offset */
  0, /* Y offset */
  0, /* Z offset */
};
static tAccelCal sCalValues; /* calibration values in RAM */

#define CalNxOff   sCalValues.NxOff
#define CalNyOff   sCalValues.NyOff
#define CalNzOff   sCalValues.NzOff

/*
** ===================================================================
**     Method      :  Acelerometro_GetRaw8XYZ (component MMA8451Q)
**     Description :
**         Returns in an array the x, y and z accelerometer as 8bit
**         values.
**     Parameters  :
**         NAME            - DESCRIPTION
**       * xyz             - Pointer to an array of three unsigned 8bit
**                           values which are used to return the
**                           accelerometer values.
**     Returns     :
**         ---             - Error code, ERR_OK for no error.
** ===================================================================
*/
uint8_t Acelerometro_GetRaw8XYZ(uint8_t *xyz)
{
  static const uint8_t addr = Acelerometro_OUT_X_MSB;

  return GI2C1_ReadAddress(Acelerometro_I2C_ADDR, (uint8_t*)&addr, sizeof(addr), &xyz[0], 3);
}

