/*
** ===================================================================
**     Method      :  Acelerometro_SetFastMode (component MMA8451Q)
**     Description :
**         Turns the F_READ (Fast Read Mode) on or off
**     Parameters  :
**         NAME            - DESCRIPTION
**         on              - if to turn the F_READ mode on or off
**     Returns     :
**         ---             - Error code
** ===================================================================
*/
byte Acelerometro_SetFastMode(bool on)
{
  uint8_t val, res;

  res = GI2C1_ReadByteAddress8(Acelerometro_I2C_ADDR, Acelerometro_CTRL_REG_1, &val);
  if (res!=ERR_OK) {
    return res;
  }
  if (on) {
    val |= Acelerometro_F_READ_BIT_MASK; /* enable F_READ: Fast read mode, data format limited to single byte (auto increment counter will skip LSB) */
  } else {
    val &= ~Acelerometro_F_READ_BIT_MASK; /* disable F_READ: Fast read mode, data format limited to single byte (auto increment counter will skip LSB) */
  }
  return GI2C1_WriteByteAddress8(Acelerometro_I2C_ADDR, Acelerometro_CTRL_REG_1, val);
}

/*
** ===================================================================
**     Method      :  Acelerometro_Enable (component MMA8451Q)
**     Description :
**         Enables the device with setting the ACTIVE bit in the CTRL
**         register 1
**     Parameters  : None
**     Returns     :
**         ---             - Error code
** ===================================================================
*/
byte Acelerometro_Enable(void)
{
  uint8_t val, res;

  res = GI2C1_ReadByteAddress8(Acelerometro_I2C_ADDR, Acelerometro_CTRL_REG_1, &val);
  if (res!=ERR_OK) {
    return res;
  }
  val |= Acelerometro_ACTIVE_BIT_MASK; /* enable device */
  return GI2C1_WriteByteAddress8(Acelerometro_I2C_ADDR, Acelerometro_CTRL_REG_1, val);
}

/*
** ===================================================================
**     Method      :  Acelerometro_Disable (component MMA8451Q)
**     Description :
**         Disables the device with clearing the ACTIVE bit in the CTRL
**         register 1
**     Parameters  : None
**     Returns     :
**         ---             - Error code
** ===================================================================
*/
byte Acelerometro_Disable(void)
{
  uint8_t val, res;

  res = GI2C1_ReadByteAddress8(Acelerometro_I2C_ADDR, Acelerometro_CTRL_REG_1, &val);
  if (res!=ERR_OK) {
    return res;
  }
  val &= ~Acelerometro_ACTIVE_BIT_MASK; /* disable device */
  return GI2C1_WriteByteAddress8(Acelerometro_I2C_ADDR, Acelerometro_CTRL_REG_1, val);
}

/*
** ===================================================================
**     Method      :  Acelerometro_isEnabled (component MMA8451Q)
**     Description :
**         Returns the status of the the ACTIVE bit in the CTRL
**         register 1
**     Parameters  :
**         NAME            - DESCRIPTION
**       * isEnabled       - Pointer to where to store the
**                           result, TRUE if ACTIVE bit is set, FALSE
**                           otherwise
**     Returns     :
**         ---             - error code
** ===================================================================
*/
byte Acelerometro_isEnabled(bool *isEnabled)
{
  uint8_t val, res;

  res = GI2C1_ReadByteAddress8(Acelerometro_I2C_ADDR, Acelerometro_CTRL_REG_1, &val);
  if (res!=ERR_OK) {
    return ERR_FAILED;
  }
  *isEnabled = (val&Acelerometro_ACTIVE_BIT_MASK)!=0; /* TRUE if bit is set, FALSE otherwise */
  return ERR_OK;
}

